import React, { Component } from 'react';
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import classnames from 'classnames';
import { loginUser } from '../../actions/authActions';




class Login extends Component {
  constructor(){
    super();
    this.state={
      email: '',
      password: '', 
      errors: {}
    };

    this.onChange= this.onChange.bind(this);
    this.onSubmit= this.onSubmit.bind(this);
  }
  componentDidMount() {
    if (this.props.auth.isAuthenticated) {
      this.props.history.push('/UserProfile');
    }
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      this.props.history.push('/UserProfile');
    }

    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }

  

  onSubmit(e){
    e.preventDefault();

    const userData={
      email:this.state.email,
      password:this.state.password
    };
    this.props.loginUser(userData)
  }
  onChange(e){
      this.setState({ [e.target.name]: e.target.value});
    }
  render() {
    const {errors}= this.state;

    return (
    <div className="Login">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <h1 className="display-4 text-center">Login</h1>
              <p className="lead text-center">Sign into your Quest account</p>
              <form onSubmit={this.onSubmit} history={this.props.history}>
                <div className="form-group">
                  <input type="email" 
                  className={classnames("form-control form-control-lg",{
                    'is-invalid': errors.email
                  })} placeholder="Email Address" name="email" value={this.state.email} 
                  onChange={this.onChange}/>
                  {errors.email && (<div className="invalid-feedback">{errors.email}</div>)}
                </div>
                <div className="form-group">
                  <input type="password" 
                  className={classnames("form-control form-control-lg",{
                    'is-invalid': errors.password
                  })}
                  placeholder="Password" name="password" value={this.state.password} 
                  onChange={this.onChange}/>
                  {errors.password && (<div className="invalid-feedback">{errors.password}</div>)}
                </div>
                <input type="submit" className="btn btn-col btn-info btn-block mt-4" />
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
Login.propTypes={
  loginUser:PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
}

const mapStateToProps =(state) =>({
  auth: state.auth,
  errors: state.errors
})

export default connect(mapStateToProps, {loginUser})(Login);
